const isValidMove = (piece, fromRow, fromCol, toRow, toCol, board, isWhiteTurn, isBlackTurn) => {
  if (!piece) return false;
  if (fromRow === toRow && fromCol === toCol) return false; // Không di chuyển đến vị trí hiện tại

  // ✅ **Xác định quân trắng hay đen**
  const isWhite = ["♙", "♖", "♘", "♗", "♕", "♔"].includes(piece);

  // ✅ **Kiểm tra đúng lượt chơi**
  if ((isWhite && !isWhiteTurn) || (!isBlack && !isBlackTurn)) return false;

  const targetPiece = board[toRow][toCol];

  // ✅ **Kiểm tra có ăn quân cùng màu không**
  if (targetPiece && ["♙", "♖", "♘", "♗", "♕", "♔", "♟", "♜", "♞", "♝", "♛", "♚"].includes(targetPiece) === isWhite) return false;

  switch (piece) {
    case "♙": return isValidPawnMove(fromRow, fromCol, toRow, toCol, board, true);
    case "♟": return isValidPawnMove(fromRow, fromCol, toRow, toCol, board, false);
    case "♖": case "♜": return isValidRookMove(fromRow, fromCol, toRow, toCol, board);
    case "♘": case "♞": return isValidKnightMove(toRow - fromRow, toCol - fromCol);
    case "♗": case "♝": return isValidBishopMove(fromRow, fromCol, toRow, toCol, board);
    case "♕": case "♛": return isValidQueenMove(fromRow, fromCol, toRow, toCol, board);
    case "♔": case "♚": return isValidKingMove(toRow - fromRow, toCol - fromCol);
    default: return false;
  }
};


const isValidPawnMove = (fromRow, fromCol, toRow, toCol, board, isWhite) => {
  const direction = isWhite ? -1 : 1;
  const startRow = isWhite ? 6 : 1;
  const dy = toRow - fromRow;
  const dx = Math.abs(toCol - fromCol);

  if (dx === 0) {
    if (dy === direction && !board[toRow][toCol]) return true;
    if (fromRow === startRow && dy === 2 * direction && !board[toRow][toCol] && !board[fromRow + direction][toCol]) return true;
  }

  if (dx === 1 && dy === direction && board[toRow][toCol]) return true;

  return false;
};

const isValidRookMove = (fromRow, fromCol, toRow, toCol, board) => {
  if (fromRow !== toRow && fromCol !== toCol) return false;
  return isPathClear(fromRow, fromCol, toRow, toCol, board);
};

const isValidKnightMove = (dx, dy) => {
  return (Math.abs(dx) === 2 && Math.abs(dy) === 1) || (Math.abs(dx) === 1 && Math.abs(dy) === 2);
};

const isValidBishopMove = (fromRow, fromCol, toRow, toCol, board) => {
  if (Math.abs(fromRow - toRow) !== Math.abs(fromCol - toCol)) return false;
  return isPathClear(fromRow, fromCol, toRow, toCol, board);
};

const isValidQueenMove = (fromRow, fromCol, toRow, toCol, board) => {
  return (
    isValidRookMove(fromRow, fromCol, toRow, toCol, board) ||
    isValidBishopMove(fromRow, fromCol, toRow, toCol, board)
  );
};

const isValidKingMove = (dx, dy) => {
  return Math.abs(dx) <= 1 && Math.abs(dy) <= 1;
};

const isPathClear = (fromRow, fromCol, toRow, toCol, board) => {
  const dx = Math.sign(toCol - fromCol);
  const dy = Math.sign(toRow - fromRow);
  let x = fromCol + dx;
  let y = fromRow + dy;

  while (x !== toCol || y !== toRow) {
    if (board[y][x]) return false;
    x += dx;
    y += dy;
  }
  return true;
};

const checkGameOver = (board) => {
  let whiteKingExists = false;
  let blackKingExists = false;

  for (let row of board) {
    for (let piece of row) {
      if (piece === "\u2654") whiteKingExists = true;
      if (piece === "\u265A") blackKingExists = true;
    }
  }

  if (!whiteKingExists) return "Black wins!";
  if (!blackKingExists) return "White wins!";
  return null;
};

export { isValidMove, checkGameOver };
