import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AdminPage = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    // Kiểm tra nếu user không phải admin -> quay lại trang login admin
    const isAdmin = localStorage.getItem("isAdmin");
    if (isAdmin !== "true") {
      alert("You must be an admin to access this page!");
      navigate("/admin-login");
    }

    // Lấy danh sách người dùng từ localStorage
    const storedUsers = localStorage.getItem("users");
    if (storedUsers) {
      setUsers(JSON.parse(storedUsers)); // Chuyển JSON thành mảng user
    }
  }, [navigate]);

  // Khóa tài khoản
  const handleBlockUser = (email) => {
    const updatedUsers = users.map((user) =>
      user.email === email ? { ...user, isBlocked: true } : user
    );
    setUsers(updatedUsers);
    localStorage.setItem("users", JSON.stringify(updatedUsers));
    alert(`User ${email} has been blocked!`);
  };

  // Mở khóa tài khoản
  const handleUnblockUser = (email) => {
    const updatedUsers = users.map((user) =>
      user.email === email ? { ...user, isBlocked: false } : user
    );
    setUsers(updatedUsers);
    localStorage.setItem("users", JSON.stringify(updatedUsers));
    alert(`User ${email} has been unblocked!`);
  };

  // Xem thông tin chi tiết của người dùng
  const handleCheckUser = (user) => {
    setSelectedUser(user);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h2 className="text-3xl font-semibold text-center text-gray-800 mb-6">
        Admin Dashboard
      </h2>

      <div className="max-w-4xl mx-auto bg-white shadow-lg rounded-lg p-6">
        <h3 className="text-xl font-bold mb-4">User List</h3>

        {users.length > 0 ? (
          <table className="w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-200">
                <th className="border border-gray-300 px-4 py-2">Email</th>
                <th className="border border-gray-300 px-4 py-2">Status</th>
                <th className="border border-gray-300 px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr key={index} className="text-center">
                  <td className="border border-gray-300 px-4 py-2">{user.email}</td>
                  <td className="border border-gray-300 px-4 py-2">
                    {user.isBlocked ? "Blocked" : "Active"}
                  </td>
                  <td className="border border-gray-300 px-4 py-2 space-x-2">
                    {!user.isBlocked ? (
                      <button
                        onClick={() => handleBlockUser(user.email)}
                        className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                      >
                        Block
                      </button>
                    ) : (
                      <button
                        onClick={() => handleUnblockUser(user.email)}
                        className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                      >
                        Unblock
                      </button>
                    )}
                    <button
                      onClick={() => handleCheckUser(user)}
                      className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                    >
                      Check
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="text-center text-gray-600">No users found.</p>
        )}

        {/* Hiển thị thông tin chi tiết khi nhấn Check */}
        {selectedUser && (
          <div className="mt-6 p-4 bg-gray-100 rounded-lg">
            <h3 className="text-lg font-bold mb-2">User Details</h3>
            <p><strong>Email:</strong> {selectedUser.email}</p>
            <p><strong>Password:</strong> {selectedUser.password}</p>
            <p>
              <strong>Status:</strong> {selectedUser.isBlocked ? "Blocked" : "Active"}
            </p>
            <button
              onClick={() => setSelectedUser(null)}
              className="mt-3 px-4 py-1 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
              Close
            </button>
          </div>
        )}

        <div className="flex justify-center mt-6">
          <button
            onClick={() => {
              localStorage.removeItem("isAdmin"); // Đăng xuất admin
              navigate("/homepage");
            }}
            className="px-4 py-2 bg-red-500 text-white font-semibold rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
