import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ResetPassword = () => {
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const navigate = useNavigate();

  const handlePasswordReset = (e) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    const resetEmail = localStorage.getItem("resetEmail");
    if (!resetEmail) {
      alert("Invalid request! Please try again.");
      navigate("/forgot-password");
      return;
    }

    const storedUsers = JSON.parse(localStorage.getItem("users")) || [];
    const updatedUsers = storedUsers.map((user) =>
      user.email === resetEmail ? { ...user, password: newPassword } : user
    );

    localStorage.setItem("users", JSON.stringify(updatedUsers));
    localStorage.removeItem("resetEmail");

    alert("Password reset successfully!");
    navigate("/signin");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white shadow-lg rounded-xl flex flex-col md:flex-row items-center p-6 max-w-4xl">
        {/* Hình ảnh minh họa */}
        <div className="w-full md:w-1/2 flex justify-center">
          <img
            src="/mnt/data/image.png" // Đường dẫn chính xác đến ảnh minh họa
            alt="Reset Password Illustration"
            className="w-full max-w-sm"
          />
        </div>

        {/* Form nhập mật khẩu mới */}
        <div className="w-full md:w-1/2 bg-white p-6 rounded-lg shadow-md">
          <div className="text-center mb-6">
            <img
              src="https://cdn-icons-png.flaticon.com/512/3064/3064197.png"
              alt="Lock Icon"
              className="w-12 mx-auto"
            />
            <h2 className="text-2xl font-bold text-gray-800">Reset Your Password</h2>
          </div>

          <form onSubmit={handlePasswordReset} className="space-y-4">
            {/* New Password */}
            <div>
              <input
                type="password"
                placeholder="Password"
                className="w-full px-4 py-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 outline-none"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
            </div>

            {/* Confirm Password */}
            <div>
              <input
                type="password"
                placeholder="Confirm Password"
                className="w-full px-4 py-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 outline-none"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>

            {/* Change Password Button */}
            <button
              type="submit"
              className="w-full bg-red-500 text-white py-3 rounded-md font-bold hover:bg-red-600 transition duration-300"
            >
              Change
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
