import React, { useEffect, useState } from "react";

const WatchMatch = () => {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch("/matches.json") // Lấy dữ liệu từ file JSON
      .then((res) => res.json())
      .then((data) => setMatches(data));
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold text-red-600 mb-6">Xem người khác chơi</h1>
      
      <div className="grid grid-cols-2 gap-6">
        {matches.map((match) => (
          <div key={match.match_id} className="bg-white p-4 shadow-md rounded-lg flex items-center space-x-4">
            {/* Avatar */}
            <div className="w-12 h-12 flex items-center justify-center rounded-full text-white font-bold text-lg"
                 style={{ backgroundColor: match.player1.color }}>
              {match.player1.name.charAt(0).toUpperCase()}
            </div>

            {/* Thông tin người chơi */}
            <div className="flex-1">
              <div className="flex items-center space-x-2">
                <span className="font-semibold text-lg">{match.player1.name}</span>
                <img src={match.player1.flag} alt={match.player1.country} className="w-5 h-3" />
              </div>
              <p className="text-gray-600 text-sm">{match.player1.rating}</p>

              <div className="flex items-center space-x-2 text-sm text-gray-700 mt-2">
                <span className="text-yellow-500">★</span>
                <span>Tính điểm</span>
                <span className="ml-auto flex items-center space-x-1">
                  <img src="/sword-icon.png" alt="Game Mode" className="w-4 h-4" />
                  <span>{match.time_control}</span>
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchMatch;
