import React, { useState, useEffect } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { getValidMoves } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate, findKingPosition } from "../utils/checkmate";
import pieceImages from "../assets/pieceImages";

const ItemTypes = { PIECE: "piece" };

const Square = ({ piece, row, col, movePiece, isKingInCheck, selectedPiece, setSelectedPiece, currentTurn, board, validMoves }) => {
  const isDark = (row + col) % 2 === 1;
  const isSelected = selectedPiece && selectedPiece.row === row && selectedPiece.col === col;
  const isValidMoveSquare = validMoves.some((move) => move.row === row && move.col === col);
  const isCaptureMove = isValidMoveSquare && board[row][col];

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  const handleClick = () => {
    if (selectedPiece) {
      const isValidMove = validMoves.some((move) => move.row === row && move.col === col);
      if (isValidMove) {
        movePiece(selectedPiece.row, selectedPiece.col, row, col);
      }
      setSelectedPiece(null);
    } else if (piece) {
      const isWhite = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);
      if (isWhite === currentTurn) {
        setSelectedPiece({ row, col, piece });
      }
    }
  };

  return (
    <div
      ref={drop}
      className={`w-16 h-16 flex justify-center items-center 
        ${isDark ? "bg-[#9A6553]" : "bg-[#D5B793]"}
        ${isKingInCheck ? "bg-red-600 animate-pulse" : ""}
        ${isSelected ? "border-4 border-yellow-500" : ""}
        ${isValidMoveSquare ? "border-4 border-green-500" : ""}
        ${isCaptureMove ? "border-4 border-red-500" : ""}
      `}
      onClick={handleClick}
    >
      {piece && pieceImages[piece] && (
        <img
          ref={drag}
          src={pieceImages[piece]}
          alt={piece}
          className={`w-12 h-12 object-contain ${isDragging ? "opacity-50" : "cursor-pointer"}`}
        />
      )}
    </div>

  );
};

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [moveHistory, setMoveHistory] = useState([]);
  const [currentTurn, setCurrentTurn] = useState(true);
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);
  const [checkedKing, setCheckedKing] = useState(null);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [validMoves, setValidMoves] = useState([]);
  const [gameMessage, setGameMessage] = useState("");
  // ‚úÖ Th√™m h√†m resetGame ngay t·∫°i ƒë√¢y
  const resetGame = () => {
    setBoard(initialBoardSetup);
    setCurrentTurn(true);
    setIsCheck(false);
    setIsCheckMate(false);
    setCheckedKing(null);
    setSelectedPiece(null);
    setValidMoves([]);
    setGameMessage("üé≠ New Game Started! White's Turn");
  };
  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];

      // Validate move
      const validMoves = getValidMoves(piece, fromRow, fromCol, prevBoard);
      if (!validMoves.some((move) => move.row === toRow && move.col === toCol)) {
        return prevBoard;
      }

      // Update board
      newBoard[toRow][toCol] = piece;
      newBoard[fromRow][fromCol] = "";

      return newBoard;
    });

    setTimeout(() => {
      setBoard((updatedBoard) => {
        const nextTurn = !currentTurn;
        const kingPos = findKingPosition(updatedBoard, nextTurn);
        console.log("‚ôüÔ∏è V·ªã tr√≠ vua m·ªõi:", kingPos);

        const kingCheck = isKingInCheck(updatedBoard, nextTurn);
        const checkmate = isCheckmate(updatedBoard, nextTurn);

        console.log("‚ôüÔ∏è Checking for check:", kingCheck);

        setCurrentTurn(nextTurn);
        setIsCheck(kingCheck);
        setCheckedKing(kingCheck ? kingPos : null);
        setIsCheckMate(checkmate);
        setSelectedPiece(null);
        setValidMoves([]);

        if (checkmate) {
          setGameMessage(`üèÜ Checkmate! ${currentTurn ? "Black Wins!" : "White Wins!"} üèÜ`);
        } else if (kingCheck) {
          setGameMessage(`üî• ${currentTurn ? "White" : "Black"} King is in check! üî•`);
        } else {
          setGameMessage(`üé≠ Current Turn: ${nextTurn ? "White" : "Black"}`);
        }

        setMoveHistory((prev) => [
          ...prev,
          `${convertToChessNotation(fromRow, fromCol)} ‚ûù ${convertToChessNotation(toRow, toCol)}`
        ]);

        return updatedBoard;
      });
    }, 100);
  };

  useEffect(() => {
    const kingCheck = isKingInCheck(board, currentTurn);
    const checkmate = isCheckmate(board, currentTurn);
    const kingPos = findKingPosition(board, currentTurn);

    setIsCheck(kingCheck);
    setIsCheckMate(checkmate);
    setCheckedKing(kingCheck ? kingPos : null);

    // ‚úÖ C·∫≠p nh·∫≠t th√¥ng b√°o UI
    if (checkmate) {
        setGameMessage(`üèÜ Checkmate! ${currentTurn ? "Black" : "White"} Wins!`);
    } else if (kingCheck) {
        setGameMessage(`üî• Warning! ${currentTurn ? "White" : "Black"} King is in Check! üî•`);
    } else {
        setGameMessage(`üé≠ Current Turn: ${currentTurn ? "White" : "Black"}`);
    }

}, [board, currentTurn]); // Ch·∫°y l·∫°i khi b√†n c·ªù ho·∫∑c l∆∞·ª£t ch∆°i thay ƒë·ªïi





  const convertToChessNotation = (row, col) => {
    const files = ["a", "b", "c", "d", "e", "f", "g", "h"];
    return `${files[col]}${8 - row}`;
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen flex flex-col justify-center items-center bg-gray-200">
        <h1 className="text-2xl font-bold mb-4">‚ôüÔ∏è Chess Game ‚ôüÔ∏è</h1>

        {gameMessage && (
          <div className="mb-2 bg-yellow-300 text-black px-4 py-2 rounded text-center font-bold shadow-md">
            {gameMessage}
          </div>
        )}
        {/* Checkmate Message */}
        {isCheckMate && (
          <div className="fixed top-0 left-0 w-full h-full flex justify-center items-center bg-black bg-opacity-75">
            <div className="bg-white p-6 rounded-lg shadow-xl text-center">
              <h2 className="text-2xl font-bold text-red-600">üèÜ Checkmate! {currentTurn ? "Black" : "White"} Wins! üèÜ</h2>
              <button 
                onClick={resetGame}
                className="mt-4 px-4 py-2 bg-blue-500 text-white font-bold rounded hover:bg-blue-600"
              >
                Play Again
              </button>
            </div>
          </div>
        )}

        <div className="grid grid-cols-8 border-4 border-black shadow-lg">
          {board.map((row, rowIndex) =>
            row.map((piece, colIndex) => (
              <Square
                key={`${rowIndex}-${colIndex}`}
                piece={piece}
                row={rowIndex}
                col={colIndex}
                movePiece={movePiece}
                isKingInCheck={checkedKing?.row === rowIndex && checkedKing?.col === colIndex}
                selectedPiece={selectedPiece}
                setSelectedPiece={setSelectedPiece}
                currentTurn={currentTurn}
                board={board}
                validMoves={selectedPiece ? getValidMoves(selectedPiece.piece, selectedPiece.row, selectedPiece.col, board) : []}
              />
            ))
          )}
        </div>
      </div>
    </DndProvider>
  );
};
const handleMove = (fromRow, fromCol, toRow, toCol) => {
  const newBoard = JSON.parse(JSON.stringify(board)); // Sao ch√©p b√†n c·ªù
  const movingPiece = newBoard[fromRow][fromCol];

  if (!isValidMove(movingPiece, fromRow, fromCol, toRow, toCol, board)) {
      console.log("‚ùå N∆∞·ªõc ƒëi kh√¥ng h·ª£p l·ªá");
      return;
  }

  // Th·ª±c hi·ªán n∆∞·ªõc ƒëi
  newBoard[toRow][toCol] = movingPiece;
  newBoard[fromRow][fromCol] = "";

  setBoard(newBoard);
  setIsWhiteTurn(!isWhiteTurn); // ƒê·ªïi l∆∞·ª£t ch∆°i

  // Ki·ªÉm tra chi·∫øu & chi·∫øu h·∫øt
  if (isKingInCheck(newBoard, !isWhiteTurn)) {
      console.log(`‚ö†Ô∏è Vua ƒëang b·ªã chi·∫øu`);
      setGameMessage(`‚ö†Ô∏è King is in check!`);

      if (isCheckmate(newBoard, !isWhiteTurn)) {
          console.log(`üèÜ Chi·∫øu h·∫øt! Tr√≤ ch∆°i k·∫øt th√∫c.`);
          setGameMessage(`üèÜ Checkmate! ${isWhiteTurn ? "White" : "Black"} wins!`);
          setGameOver(true);
      }
  } else {
      setGameMessage("");
  }
};


export default Chessboard;
