import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const SOCKET_URL = "http://localhost:8080/ws"; // URL backend WebSocket
let stompClient = null;

export const connectWebSocket = (onMoveReceived) => {
  if (stompClient && stompClient.connected) {
    console.log("‚ö° WebSocket already connected");
    return;
  }

  const socket = new SockJS(SOCKET_URL);
  stompClient = new Client({
    webSocketFactory: () => socket,
    reconnectDelay: 5000, 
    debug: (msg) => console.log("üîç STOMP Debug:", msg),
    onConnect: () => {
      console.log("‚úÖ WebSocket Connected");

      const subscription = stompClient.subscribe("/topic/chess-updates", (message) => {
        console.log("üì© Subscription received data:", message.body);
        try {
          const moveData = JSON.parse(message.body);
          console.log("‚ôüÔ∏è Move Received:", moveData);

          if (moveData && moveData.move && moveData.move.fromRow !== undefined) {
            onMoveReceived(moveData);
          } else {
            console.warn("‚ö†Ô∏è Invalid move data received:", moveData);
          }
        } catch (error) {
          console.error("‚ùå Error parsing received move:", error);
        }
      });

      console.log("üìå Subscription ID:", subscription.id);
    },
    onDisconnect: () => console.log("‚ùå WebSocket Disconnected"),
    onStompError: (frame) => console.error("üö® STOMP Error:", frame),
  });

  stompClient.activate();
};

export const sendMove = (fromRow, fromCol, toRow, toCol, piece, player) => {
  if (!stompClient || !stompClient.connected) {
      console.warn("‚ö†Ô∏è WebSocket ch∆∞a k·∫øt n·ªëi!");
      return;
  }

  const moveData = {
      player: player, // "white" ho·∫∑c "black"
      move: {
          fromRow: fromRow,
          fromCol: fromCol,
          toRow: toRow,
          toCol: toCol,
          piece: piece
      }
  };

  console.log("üì§ G·ª≠i d·ªØ li·ªáu:", JSON.stringify(moveData));

  stompClient.publish({
    destination: "/app/move",
    headers: { "content-type": "application/json" },
    body: JSON.stringify(moveData),
});


  console.log("‚úÖ Move sent to /app/move");
};
