import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const SOCKET_URL = "http://localhost:8080/ws"; // URL backend WebSocket
let stompClient = null;

export const connectWebSocket = (onMoveReceived) => {
  if (stompClient && stompClient.connected) {
    console.log("âš¡ WebSocket already connected");
    return;
  }

  const socket = new SockJS(SOCKET_URL);
  stompClient = new Client({
    webSocketFactory: () => socket,
    reconnectDelay: 5000, 
    debug: (msg) => console.log("ðŸ” STOMP Debug:", msg),
    onConnect: () => {
      console.log("âœ… WebSocket Connected");

      const subscription = stompClient.subscribe("/topic/chess-updates", (message) => {
        try {
          const moveData = JSON.parse(message.body);
          console.log("â™Ÿï¸ Move Received:", moveData);

          if (moveData && moveData.move && moveData.move.fromRow !== undefined) {
            onMoveReceived(moveData);
          } else {
            console.warn("âš ï¸ Invalid move data received:", moveData);
          }
        } catch (error) {
          console.error("âŒ Error parsing received move:", error);
        }
      });

      console.log("ðŸ“Œ Subscription ID:", subscription.id);
    },
    onDisconnect: () => console.log("âŒ WebSocket Disconnected"),
    onStompError: (frame) => console.error("ðŸš¨ STOMP Error:", frame),
  });

  stompClient.activate();
};
