import React, { useState, useEffect } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { getValidMoves } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate, findKingPosition } from "../utils/checkmate";
import pieceImages from "../assets/pieceImages";
import { Client } from "@stomp/stompjs";

const ItemTypes = { PIECE: "piece" };

const Square = ({ piece, row, col, movePiece, isKingInCheck, selectedPiece, setSelectedPiece, currentTurn, board, validMoves }) => {
  const isDark = (row + col) % 2 === 1;
  const isSelected = selectedPiece && selectedPiece.row === row && selectedPiece.col === col;
  const isValidMoveSquare = validMoves.some((move) => move.row === row && move.col === col);
  const isCaptureMove = isValidMoveSquare && board[row][col];

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  const handleClick = () => {
    if (selectedPiece) {
      const isValidMove = validMoves.some((move) => move.row === row && move.col === col);
      if (isValidMove) {
        movePiece(selectedPiece.row, selectedPiece.col, row, col);
      }
      setSelectedPiece(null);
    } else if (piece) {
      const isWhite = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);
      if (isWhite === currentTurn) {
        setSelectedPiece({ row, col, piece });
      }
    }
  };

  return (
    <div
      ref={drop}
      className={`w-16 h-16 flex justify-center items-center 
        ${isDark ? "bg-[#9A6553]" : "bg-[#D5B793]"}
        ${isKingInCheck ? "bg-red-600 animate-pulse" : ""}
        ${isSelected ? "border-4 border-yellow-500" : ""}
        ${isValidMoveSquare ? "border-4 border-green-500" : ""}
        ${isCaptureMove ? "border-4 border-red-500" : ""}
      `}
      onClick={handleClick}
    >
      {piece && pieceImages[piece] && (
        <img
          ref={drag}
          src={pieceImages[piece]}
          alt={piece}
          className={`w-12 h-12 object-contain ${isDragging ? "opacity-50" : "cursor-pointer"}`}
        />
      )}
    </div>
  );
};

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [currentTurn, setCurrentTurn] = useState(true);
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);
  const [checkedKing, setCheckedKing] = useState(null);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [validMoves, setValidMoves] = useState([]);
  const [gameMessage, setGameMessage] = useState("");
  const [client, setClient] = useState(null);

  useEffect(() => {
    const stompClient = new Client({
      brokerURL: "ws://localhost:8080/chess", // ƒê·ªãa ch·ªâ WebSocket
      onConnect: () => {
        console.log("‚úÖ WebSocket connected!");
        stompClient.subscribe("/topic/move", (message) => {
          const moveData = JSON.parse(message.body);
          movePiece(moveData.fromRow, moveData.fromCol, moveData.toRow, moveData.toCol, false);
        });
      },
      onDisconnect: () => console.log("üî¥ WebSocket disconnected."),
      onStompError: (frame) => console.error("‚ùå WebSocket error:", frame),
    });
    stompClient.activate();
    setClient(stompClient);

    return () => stompClient.deactivate();
  }, []);

  const movePiece = (fromRow, fromCol, toRow, toCol, sendMove = true) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];

      const validMoves = getValidMoves(piece, fromRow, fromCol, prevBoard);
      if (!validMoves.some((move) => move.row === toRow && move.col === toCol)) {
        return prevBoard;
      }

      newBoard[toRow][toCol] = piece;
      newBoard[fromRow][fromCol] = "";
      return newBoard;
    });

    setCurrentTurn((prev) => !prev);
    setSelectedPiece(null);
    setValidMoves([]);

    if (sendMove && client) {
      client.publish({
        destination: "/app/move",
        body: JSON.stringify({ fromRow, fromCol, toRow, toCol }),
      });
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen flex flex-col justify-center items-center bg-gray-200">
        <h1 className="text-2xl font-bold mb-4">‚ôüÔ∏è Chess Game ‚ôüÔ∏è</h1>
        {gameMessage && (
          <div className="mb-2 bg-yellow-300 text-black px-4 py-2 rounded text-center font-bold shadow-md">
            {gameMessage}
          </div>
        )}
        <div className="grid grid-cols-8 border-4 border-black shadow-lg">
          {board.map((row, rowIndex) =>
            row.map((piece, colIndex) => (
              <Square
                key={`${rowIndex}-${colIndex}`}
                piece={piece}
                row={rowIndex}
                col={colIndex}
                movePiece={movePiece}
                isKingInCheck={checkedKing?.row === rowIndex && checkedKing?.col === colIndex}
                selectedPiece={selectedPiece}
                setSelectedPiece={setSelectedPiece}
                currentTurn={currentTurn}
                board={board}
                validMoves={selectedPiece ? getValidMoves(selectedPiece.piece, selectedPiece.row, selectedPiece.col, board) : []}
              />
            ))
          )}
        </div>
      </div>
    </DndProvider>
  );
};

export default Chessboard;
