import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const SOCKET_URL = "http://localhost:8080/ws"; // URL WebSocket Backend
let stompClient = null;
let currentRoomId = null; // L∆∞u tr·ªØ ID ph√≤ng hi·ªán t·∫°i

export const connectWebSocket = (roomId, onMoveReceived) => {
  if (stompClient && stompClient.connected) {
    console.log("‚ö° WebSocket already connected");
    return;
  }

  currentRoomId = roomId; // G√°n ID ph√≤ng hi·ªán t·∫°i
  const socket = new SockJS(SOCKET_URL);
  
  stompClient = new Client({
    webSocketFactory: () => socket,
    reconnectDelay: 5000, // T·ª± ƒë·ªông k·∫øt n·ªëi l·∫°i sau 5 gi√¢y n·∫øu m·∫•t k·∫øt n·ªëi
    debug: (msg) => console.log("üîç STOMP Debug:", msg),
    onConnect: () => {
      console.log(`‚úÖ WebSocket Connected to room: ${currentRoomId}`);

      // Subscribe ƒë·∫øn topic ri√™ng c·ªßa t·ª´ng ph√≤ng
      stompClient.subscribe(`/topic/chess-room/${currentRoomId}`, (message) => {
        try {
          const moveData = JSON.parse(message.body);
          console.log("‚ôüÔ∏è Move Received:", moveData);

          if (moveData && moveData.fromRow !== undefined && moveData.toRow !== undefined) {
            onMoveReceived(moveData);
          } else {
            console.warn("‚ö†Ô∏è Invalid move data received:", moveData);
          }
        } catch (error) {
          console.error("‚ùå Error parsing received move:", error);
        }
      });
    },
    onDisconnect: () => console.log("‚ùå WebSocket Disconnected"),
    onStompError: (frame) => console.error("üö® STOMP Error:", frame),
  });

  stompClient.activate();
};

export const sendMove = (moveData) => {
  if (!stompClient || !stompClient.connected) {
    console.warn("‚ö†Ô∏è Cannot send move, WebSocket is not connected.");
    return;
  }

  if (!moveData || moveData.fromRow === undefined || moveData.toRow === undefined) {
    console.warn("‚ö†Ô∏è Invalid move data:", moveData);
    return;
  }

  stompClient.publish({
    destination: `/app/move/${currentRoomId}`, // G·ª≠i n∆∞·ªõc ƒëi v√†o ƒë√∫ng ph√≤ng
    body: JSON.stringify(moveData),
  });

  console.log("üì§ Sent Move to room:", currentRoomId, moveData);
};
