import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const SOCKET_URL = "http://localhost:8080/ws"; // URL backend WebSocket
let stompClient = null;

export const connectWebSocket = (onMoveReceived) => {
  const socket = new SockJS("http://localhost:8080/ws");
  stompClient = Stomp.over(socket);
  
  stompClient.connect({}, () => {
      console.log("âœ… WebSocket Connected");
      
      stompClient.subscribe("/topic/game", (message) => {
          if (!message.body) {
              console.error("âŒ WebSocket received empty data!");
              return;
          }
          const move = JSON.parse(message.body);
          if (!move || !move.piece) {
              console.error("âŒ Invalid move received:", move);
              return;
          }
          console.log("ðŸ“¥ Move Received:", move);
          onMoveReceived(move); // Cáº­p nháº­t UI
      });
  }, (error) => {
      console.error("âŒ WebSocket Error:", error);
  });
};

export const sendMove = (move) => {
  if (!stompClient || !stompClient.connected) {
      console.error("âŒ WebSocket is not connected!");
      return;
  }
  if (!move.piece) {
      console.error("âŒ Move is missing piece data!");
      return;
  }
  console.log("ðŸ“¤ Sending Move:", move);
  stompClient.send("/app/move", {}, JSON.stringify(move));
};

export const disconnectWebSocket = () => {
  if (stompClient) {
      stompClient.disconnect(() => {
          console.log("ðŸ”Œ WebSocket Disconnected");
      });
  }
};
