import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../config/api"; // Import API function

const SignInForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const data = await loginUser(email, password);
      alert("Login successful!");
      localStorage.setItem("token", data.token); // Save token
      navigate("/dashboard"); // Redirect after login
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-green-100">
      <div className="w-full max-w-md p-8 bg-white rounded-2xl shadow-lg">
        <h2 className="text-2xl font-bold text-center text-green-700 mb-6">Log In</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <button type="submit" className="w-full bg-green-700 text-white py-2 rounded-md">Log In</button>
        </form>
      </div>
    </div>
  );
};

export default SignInForm;
