import React, { useState, useEffect } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { getValidMoves } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate, findKingPosition } from "../utils/checkmate";
import pieceImages from "../assets/pieceImages";
import { connectWebSocket, sendMove } from "../websocket"; // Import WebSocket
import { useNavigate } from "react-router-dom";
const ItemTypes = { PIECE: "piece" };
const ChessGame = () => {
  const [roomId, setRoomId] = useState("");
  const [connected, setConnected] = useState(false);
  const [playerColor, setPlayerColor] = useState(null);
  const [ws, setWs] = useState(null);

  const joinRoom = () => {
    const newWs = new WebSocket(`ws://localhost:8080/chess/${roomId}`);

    newWs.onopen = () => {
      console.log("Connected to room:", roomId);
      setConnected(true);
      setWs(newWs);
    };

    newWs.onmessage = (message) => {
      if (message.data.startsWith("color:")) {
        setPlayerColor(message.data.split(":")[1]);
      } else if (message.data === "room_full") {
        alert("Ph√≤ng ƒë√£ ƒë·∫ßy, vui l√≤ng nh·∫≠p ph√≤ng kh√°c!");
      }
    };
  };


  return (
    <div className="flex flex-col items-center min-h-screen justify-center bg-gray-200">
      {!connected ? (
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-bold mb-4">üîë Enter Room ID</h2>
          <input
            type="text"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="border p-2 rounded w-full mb-4"
            placeholder="Enter Room ID"
          />
          <button
            onClick={joinRoom}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 w-full"
          >
            Join Room
          </button>
        </div>
      ) : (
        <Chessboard roomId={roomId} playerColor={playerColor} ws={ws} />
      )}
    </div>
  );
};


const Square = ({ piece, row, col, movePiece, isKingSquare, selectedPiece, setSelectedPiece, currentTurn, board, validMoves }) => {
  const isDark = (row + col) % 2 === 1;
  const isSelected = selectedPiece && selectedPiece.row === row && selectedPiece.col === col;
  const isValidMoveSquare = validMoves.some((move) => move.row === row && move.col === col);
  const isCaptureMove = isValidMoveSquare && board[row][col];

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  const handleClick = () => {
    if (selectedPiece) {
      if (isValidMoveSquare) {
        movePiece(selectedPiece.row, selectedPiece.col, row, col);
      }
      setSelectedPiece(null);
    } else if (piece) {
      const isWhite = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);
      if (isWhite === currentTurn) {
        setSelectedPiece({ row, col, piece });
      }
    }
  };

  return (
    <div
      ref={drop}
      className={`w-16 h-16 flex justify-center items-center 
        ${isDark ? "bg-[#9A6553]" : "bg-[#D5B793]"}
        ${isKingSquare ? "bg-red-500 animate-pulse" : ""}
        ${isSelected ? "border-4 border-yellow-500" : ""}
        ${isValidMoveSquare ? "border-4 border-green-500" : ""}
        ${isCaptureMove ? "border-4 border-red-500" : ""}
      `}
      onClick={handleClick}
    >
      {piece && pieceImages[piece] && (
        <img
          ref={drag}
          src={pieceImages[piece]}
          alt={piece}
          className={`w-12 h-12 object-contain ${isDragging ? "opacity-50" : "cursor-pointer"}`}
        />
      )}
    </div>
  );
};

const Chessboard = ({ isBlack }) => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [moveHistory, setMoveHistory] = useState([]); // üÜï L∆∞u l·ªãch s·ª≠ n∆∞·ªõc ƒëi
  const [currentTurn, setCurrentTurn] = useState(true);
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);
  const [checkedKing, setCheckedKing] = useState(null);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [validMoves, setValidMoves] = useState([]);
  const [gameMessage, setGameMessage] = useState("üé≠ New Game Started! White's Turn");
  const [ws, setWs] = useState(null);
  const navigate = useNavigate(); 

  const [showModal, setShowModal] = useState(false); 
  const resetGame = () => {
    setBoard(initialBoardSetup);
    setMoveHistory([]); // 
    setCurrentTurn(true);
    setIsCheck(false);
    setIsCheckMate(false);
    setCheckedKing(null);
    setSelectedPiece(null);
    setValidMoves([]);
    setGameMessage("üé≠ New Game Started! White's Turn");
  };
  const handleLogout = () => {
    if (ws) {
      ws.close(); 
      setWs(null); 
    }

     navigate("/Dashboard");
  };

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
        const newBoard = prevBoard.map((row) => [...row]);
        const piece = newBoard[fromRow][fromCol];

        // üîπ Ki·ªÉm tra n∆∞·ªõc ƒëi h·ª£p l·ªá
        const validMoves = getValidMoves(piece, fromRow, fromCol, prevBoard);
        if (!validMoves.some((move) => move.row === toRow && move.col === toCol)) {
            return prevBoard; 
        }

        // üîπ Di chuy·ªÉn qu√¢n c·ªù
        newBoard[toRow][toCol] = piece;
        newBoard[fromRow][fromCol] = "";

        // üîπ C·∫≠p nh·∫≠t l·ªãch s·ª≠ n∆∞·ªõc ƒëi
        const moveNotation = `${piece} ${String.fromCharCode(97 + fromCol)}${8 - fromRow} ‚Üí ${String.fromCharCode(97 + toCol)}${8 - toRow}`;
        setMoveHistory((prevHistory) => [...prevHistory, moveNotation]);

        // üîπ Chuy·ªÉn l∆∞·ª£t ƒëi (true = White, false = Black)
        const nextTurn = !currentTurn;
        setCurrentTurn(nextTurn);

        // üîπ Ki·ªÉm tra tr·∫°ng th√°i vua
        const kingPos = findKingPosition(newBoard, nextTurn);
        const kingCheck = isKingInCheck(newBoard, nextTurn);
        const checkmate = isCheckmate(newBoard, nextTurn);

        setIsCheck(kingCheck);
        setCheckedKing(kingCheck ? kingPos : null);
        setIsCheckMate(checkmate);

        // üîπ Hi·ªÉn th·ªã th√¥ng b√°o tr√≤ ch∆°i
        if (checkmate) {
