import { Client } from "@stomp/stompjs";

const WebSocketService = {
  client: null,

  connect(onMoveReceived) {
    this.client = new Client({
      brokerURL: "ws://localhost:8080/ws", // Äá»•i URL náº¿u cáº§n
      onConnect: () => {
        console.log("âœ… WebSocket connected!");

        this.client.subscribe("/topic/move", (message) => {
          if (message.body) {
            const moveData = JSON.parse(message.body);
            onMoveReceived(moveData);
          }
        });
      },
      onDisconnect: () => {
        console.log("âŒ WebSocket disconnected!");
      },
      onStompError: (frame) => {
        console.error("ðŸš¨ STOMP Error: ", frame.headers["message"]);
      },
    });

    this.client.activate();
  },

  sendMove(fromRow, fromCol, toRow, toCol) {
    if (this.client && this.client.connected) {
      const moveData = { fromRow, fromCol, toRow, toCol };
      this.client.publish({
        destination: "/app/move",
        body: JSON.stringify(moveData),
      });
    } else {
      console.error("ðŸš¨ Error: No WebSocket connection!");
    }
  },

  disconnect() {
    if (this.client) {
      this.client.deactivate();
    }
  },
};

export default WebSocketService;
