import SockJS from "sockjs-client";
import Stomp from "stompjs";

let stompClient = null;

export const connectWebSocket = (onMoveReceived) => {
    const socket = new SockJS("http://localhost:8080/ws");
    stompClient = Stomp.over(socket);
    
    stompClient.connect({}, () => {
        console.log("✅ WebSocket Connected");
        
        stompClient.subscribe("/topic/game", (message) => {
            if (!message.body) {
                console.error("❌ WebSocket received empty data!");
                return;
            }
            const move = JSON.parse(message.body);
            if (!move || !move.piece) {
                console.error("❌ Invalid move received:", move);
                return;
            }
            console.log("📥 Move Received:", move);
            onMoveReceived(move); // Cập nhật UI
        });
    }, (error) => {
        console.error("❌ WebSocket Error:", error);
    });
};

export const sendMove = (move) => {
    if (!stompClient || !stompClient.connected) {
        console.error("❌ WebSocket is not connected!");
        return;
    }
    if (!move.piece) {
        console.error("❌ Move is missing piece data!");
        return;
    }
    console.log("📤 Sending Move:", move);
    stompClient.send("/app/move", {}, JSON.stringify(move));
};

export const disconnectWebSocket = () => {
    if (stompClient) {
        stompClient.disconnect(() => {
            console.log("🔌 WebSocket Disconnected");
        });
    }
};
