import React, { useEffect, useState } from "react";

const WatchMatch = () => {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch("/matches.json") // Lấy dữ liệu từ file JSON
      .then((res) => res.json())
      .then((data) => setMatches(data));
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold text-red-600">Xem người khác chơi</h1>
      <div className="grid grid-cols-3 gap-4 mt-4">
        {matches.map((match) => (
          <div key={match.id} className="bg-white p-4 shadow rounded-lg">
            <div className="flex items-center space-x-2">
              <img src={match.player1.avatar} alt="Avatar" className="w-8 h-8 rounded-full" />
              <span className="font-semibold">{match.player1.name}</span>
              <span>{match.player1.country}</span>
              <span>vs</span>
              <span>{match.player2.country}</span>
              <span className="font-semibold">{match.player2.name}</span>
              <img src={match.player2.avatar} alt="Avatar" className="w-8 h-8 rounded-full" />
            </div>
            <p className="text-sm text-gray-600">Thể loại: {match.type}</p>
            <button className="mt-2 px-4 py-1 bg-red-500 text-white rounded">Xem</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchMatch;
