export const isValidMove = (piece, fromRow, fromCol, toRow, toCol, board, turn, lastMove) => {
  console.log(`Move check: ${piece} from (${fromRow},${fromCol}) to (${toRow},${toCol}), Turn: ${turn}`);

  if (fromRow === toRow && fromCol === toCol) return false;

  const isWhite = "â™™â™–â™˜â™—â™•â™”".includes(piece);
  if ((turn === "white" && !isWhite) || (turn === "black" && isWhite)) return false;

  const targetPiece = board[toRow][toCol];
  const isTargetWhite = targetPiece && "â™™â™–â™˜â™—â™•â™”".includes(targetPiece);

  if (targetPiece && isWhite === isTargetWhite) return false;

  switch (piece) {
    case "â™™": return isValidPawnMove(fromRow, fromCol, toRow, toCol, board, true, lastMove);
    case "â™Ÿ": return isValidPawnMove(fromRow, fromCol, toRow, toCol, board, false, lastMove);
    case "â™–": case "â™œ": return isValidRookMove(fromRow, fromCol, toRow, toCol, board);
    case "â™˜": case "â™ž": return isValidKnightMove(toCol - fromCol, toRow - fromRow);
    case "â™—": case "â™": return isValidBishopMove(fromRow, fromCol, toRow, toCol, board);
    case "â™•": case "â™›": return isValidQueenMove(fromRow, fromCol, toRow, toCol, board);
    case "â™”": case "â™š": return isValidKingMove(fromRow, fromCol, toRow, toCol, board);
    default: return false;
  }
};

// ðŸ‡ Knight move
const isValidKnightMove = (dx, dy) => {
  return (Math.abs(dx) === 2 && Math.abs(dy) === 1) || (Math.abs(dx) === 1 && Math.abs(dy) === 2);
};

// ðŸ° Rook move (straight lines)
const isValidRookMove = (fromRow, fromCol, toRow, toCol, board) => {
  if (fromRow !== toRow && fromCol !== toCol) return false;
  return isPathClear(fromRow, fromCol, toRow, toCol, board);
};

// ðŸ¹ Bishop move (diagonal)
const isValidBishopMove = (fromRow, fromCol, toRow, toCol, board) => {
  if (Math.abs(fromRow - toRow) !== Math.abs(fromCol - toCol)) return false;
  return isPathClear(fromRow, fromCol, toRow, toCol, board);
};

// ðŸ‘‘ Queen move (Rook + Bishop combined)
const isValidQueenMove = (fromRow, fromCol, toRow, toCol, board) => {
  return isValidRookMove(fromRow, fromCol, toRow, toCol, board) || isValidBishopMove(fromRow, fromCol, toRow, toCol, board);
};

// âš” King move (1 square + Castling)
const isValidKingMove = (fromRow, fromCol, toRow, toCol, board) => {
  if (Math.abs(fromCol - toCol) <= 1 && Math.abs(fromRow - toRow) <= 1) return true;
  return isValidCastling(fromRow, fromCol, toRow, toCol, board);
};

// ðŸ° Castling
const isValidCastling = (fromRow, fromCol, toRow, toCol, board) => {
  if (Math.abs(fromCol - toCol) !== 2 || fromRow !== toRow) return false;
  return true;
};

// ðŸ“ Check if path is clear
const isPathClear = (fromRow, fromCol, toRow, toCol, board) => {
  const dx = Math.sign(toCol - fromCol);
  const dy = Math.sign(toRow - fromRow);
  let x = fromCol + dx;
  let y = fromRow + dy;

  while (x !== toCol || y !== toRow) {
    if (board[y][x]) return false;
    x += dx;
    y += dy;
  }
  return true;
};

// ðŸ”Ž Check detection
export const isKingInCheck = (turn, board) => {
  let kingPos = null;
  board.forEach((row, rIdx) =>
    row.forEach((piece, cIdx) => {
      if ((turn === "white" && piece === "â™”") || (turn === "black" && piece === "â™š")) {
        kingPos = { row: rIdx, col: cIdx };
      }
    })
  );

  return board.some((row, rIdx) =>
    row.some((piece, cIdx) => {
      if (piece && ((turn === "white" && "â™Ÿâ™œâ™žâ™â™›â™š".includes(piece)) || (turn === "black" && "â™™â™–â™˜â™—â™•â™”".includes(piece)))) {
        return isValidMove(piece, rIdx, cIdx, kingPos.row, kingPos.col, board, turn);
      }
      return false;
    })
