import { Client } from '@stomp/stompjs';

const socket = new Client({
    brokerURL: 'ws://localhost:8080/ws',
    reconnectDelay: 5000, // Tá»± Ä‘á»™ng reconnect
});

socket.onConnect = () => {
    console.log('âœ… WebSocket connected');
    
    // ÄÄƒng kÃ½ nháº­n dá»¯ liá»‡u tá»« /topic/chess-updates
    socket.subscribe('/topic/chess-updates', (message) => {
        console.log('ðŸ“© Received:', message.body);
    });
};

// ðŸ‘‰ ThÃªm hÃ m ngáº¯t káº¿t ná»‘i
export const disconnectWebSocket = () => {
    if (socket) {
        socket.deactivate();
        console.log('ðŸ”´ WebSocket disconnected');
    }
};

// KÃ­ch hoáº¡t WebSocket
socket.activate();

export default socket;
