import React, { useState, useEffect } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { isValidMove } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate } from "../utils/checkmate"; // Import ki·ªÉm tra chi·∫øu
import pieceImages from "../assets/pieceImages";

const ItemTypes = { PIECE: "piece" };

const Square = ({ piece, row, col, movePiece }) => {
  const isDark = (row + col) % 2 === 1;

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  return (
    <div
      ref={drop}
      className={`w-16 h-16 flex justify-center items-center
        ${isDark ? "bg-[#9A6553]" : "bg-[#D5B793]"}`}
    >
      {piece && pieceImages[piece] && (
        <img
          ref={drag}
          src={pieceImages[piece]}
          alt={piece}
          className={`w-12 h-12 object-contain ${
            isDragging ? "opacity-50" : "cursor-grab"
          }`}
        />
      )}
    </div>
  );
};

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [moveHistory, setMoveHistory] = useState([]);
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];
      const targetPiece = newBoard[toRow][toCol];

      if (isValidMove(piece, fromRow, fromCol, toRow, toCol, newBoard)) {
        const isWhiteTurn = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);

        if (targetPiece !== "" && isWhiteTurn === "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(targetPiece)) {
          return prevBoard;
        }

        // Di chuy·ªÉn qu√¢n c·ªù
        newBoard[toRow][toCol] = piece;
        newBoard[fromRow][fromCol] = "";

        setMoveHistory((prevHistory) => [
          ...prevHistory,
          targetPiece
            ? `${piece} ${convertToChessNotation(fromRow, fromCol)} captures ${targetPiece} at ${convertToChessNotation(toRow, toCol)}`
            : `${piece} ${convertToChessNotation(fromRow, fromCol)} ‚Üí ${convertToChessNotation(toRow, toCol)}`,
        ]);

        // Ki·ªÉm tra chi·∫øu t∆∞·ªõng & chi·∫øu h·∫øt
        setIsCheck(isKingInCheck(newBoard, !isWhiteTurn));
        setIsCheckMate(isCheckmate(newBoard, !isWhiteTurn));

        return newBoard;
      }

      return prevBoard;
    });
  };

  const convertToChessNotation = (row, col) => {
    const files = ["a", "b", "c", "d", "e", "f", "g", "h"];
    return `${files[col]}${8 - row}`;
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen flex flex-col justify-center items-center bg-gray-200">
        <h1 className="text-2xl font-bold">‚ôüÔ∏è Chess Game ‚ôüÔ∏è</h1>

        {isCheck && <div className="text-red-600 text-lg font-bold">üî• Vua ƒëang b·ªã chi·∫øu! üî•</div>}
        {isCheckMate && <div className="text-red-800 text-lg font-bold">üèÜ Chi·∫øu h·∫øt! Tr√≤ ch∆°i k·∫øt th√∫c üèÜ</div>}

        <div className="grid grid-cols-8 border-4 border-black shadow-lg">
          {board.map((row, rowIndex) =>
            row.map((piece, colIndex) => (
              <Square
                key={`${rowIndex}-${colIndex}`}
                piece={piece}
                row={rowIndex}
                col={colIndex}
                movePiece={movePiece}
              />
            ))
          )}
        </div>
      </div>
    </DndProvider>
  );
};

export default Chessboard;
