import React, { useEffect, useState } from "react";

const WatchMatch = () => {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch("/matches.json") // Lấy dữ liệu từ file JSON
      .then((res) => res.json())
      .then((data) => setMatches(data));
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold text-red-600 mb-6">Xem người khác chơi</h1>
      
      <div className="grid grid-cols-2 gap-6">
        {matches.map((match) => (
          <div key={match.match_id} className="bg-white p-4 shadow-md rounded-lg relative flex flex-col justify-between h-40">
            
            {/* Người chơi 1 (Trên) */}
            <div className="flex items-center space-x-2">
              <img src={match.player1.avatar} alt="Avatar" className="w-8 h-8 rounded-full" />
              <span className="font-semibold">{match.player1.name}</span>
              <img src={match.player1.flag} alt={match.player1.country} className="w-5 h-3" />
            </div>

            {/* VS - Biểu tượng đấu */}
            <div className="flex justify-center items-center flex-1">
              <img src="/sword-icon.png" alt="VS" className="w-8 h-8" />
            </div>

            {/* Người chơi 2 (Dưới) */}
            <div className="flex items-center space-x-2 mb-3">
              <img src={match.player2.avatar} alt="Avatar" className="w-8 h-8 rounded-full" />
              <span className="font-semibold">{match.player2.name}</span>
              <img src={match.player2.flag} alt={match.player2.country} className="w-5 h-3" />
            </div>

            {/* Thông tin trận đấu */}
            <div className="flex justify-between items-center text-sm text-gray-700">
              <span className="flex items-center space-x-1">
                <img src="/clock-icon.png" alt="Time Control" className="w-4 h-4" />
                <span>{match.time_control}</span>
              </span>
              <span className="font-bold">Score: {match.score}</span>
            </div>

            {/* Nút xem trận đấu */}
            <button className="mt-3 w-full px-4 py-2 bg-red-500 text-white rounded">Xem</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchMatch;
