import React, { useState, useEffect } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { getValidMoves } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate, findKingPosition } from "../utils/checkmate";
import pieceImages from "../assets/pieceImages";
import { connectWebSocket, sendMove } from "../websocket"; // Import WebSocket

const ItemTypes = { PIECE: "piece" };

const ChessBoard = ({ roomId, isBlack }) => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [moveHistory, setMoveHistory] = useState([]); // üÜï L∆∞u l·ªãch s·ª≠ n∆∞·ªõc ƒëi
  const [currentTurn, setCurrentTurn] = useState(true); // true: Tr·∫Øng, false: ƒêen
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);
  const [checkedKing, setCheckedKing] = useState(null);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [validMoves, setValidMoves] = useState([]);
  const [gameMessage, setGameMessage] = useState("üé≠ New Game Started! White's Turn");

  const resetGame = () => {
    setBoard(initialBoardSetup);
    setMoveHistory([]); // üÜï Reset l·ªãch s·ª≠ n∆∞·ªõc ƒëi
    setCurrentTurn(true);
    setIsCheck(false);
    setIsCheckMate(false);
    setCheckedKing(null);
    setSelectedPiece(null);
    setValidMoves([]);
    setGameMessage("üé≠ New Game Started! White's Turn");
  };

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];

      // L·∫•y danh s√°ch n∆∞·ªõc ƒëi h·ª£p l·ªá
      const validMoves = getValidMoves(piece, fromRow, fromCol, prevBoard);
      if (!validMoves.some((move) => move.row === toRow && move.col === toCol)) {
        return prevBoard; // Kh√¥ng cho ph√©p ƒëi n∆∞·ªõc kh√¥ng h·ª£p l·ªá
      }

      newBoard[toRow][toCol] = piece;
      newBoard[fromRow][fromCol] = "";

      // üÜï Ki·ªÉm tra tr·∫°ng th√°i vua
      const nextTurn = !currentTurn;
      const kingPos = findKingPosition(newBoard, nextTurn);
      const kingCheck = isKingInCheck(newBoard, nextTurn);
      const checkmate = isCheckmate(newBoard, nextTurn);

      setCurrentTurn(nextTurn);
      setIsCheck(kingCheck);
      setCheckedKing(kingCheck ? kingPos : null);
      setIsCheckMate(checkmate);
      setSelectedPiece(null);
      setValidMoves([]);

      if (checkmate) {
        setGameMessage(`üèÜ Checkmate! ${currentTurn ? "Black Wins!" : "White Wins!"}`);
      } else if (kingCheck) {
        setGameMessage(`üî• ${nextTurn ? "White" : "Black"} King is in check! üî•`);
      } else {
        setGameMessage(`üé≠ Current Turn: ${nextTurn ? "White" : "Black"}`);
      }

      // üÜï C·∫≠p nh·∫≠t l·ªãch s·ª≠ n∆∞·ªõc ƒëi
      setMoveHistory((prevHistory) => [...prevHistory, `${piece} ${fromRow},${fromCol} ‚Üí ${toRow},${toCol}`]);

      // üÜï G·ª≠i n∆∞·ªõc ƒëi qua WebSocket
      sendMove(roomId, { fromRow, fromCol, toRow, toCol, piece });

      return newBoard;
    });
  };

  // üÜï Nh·∫≠n n∆∞·ªõc ƒëi t·ª´ WebSocket
  useEffect(() => {
    connectWebSocket(roomId, (moveData) => {
      setBoard((prevBoard) => {
        if (!prevBoard || !Array.isArray(prevBoard)) return prevBoard;

        const newBoard = prevBoard.map(row => [...row]);
        newBoard[moveData.toRow][moveData.toCol] = moveData.piece;
        newBoard[moveData.fromRow][moveData.fromCol] = "";
        return newBoard;
      });

      setMoveHistory((prevHistory) => [...prevHistory, `${moveData.piece} ${moveData.fromRow},${moveData.fromCol} ‚Üí ${moveData.toRow},${moveData.toCol}`]);
      setCurrentTurn((prevTurn) => !prevTurn);
    });
  }, [roomId]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen flex flex-col justify-center items-center bg-gray-200">
        <h1 className="text-2xl font-bold mb-4">‚ôüÔ∏è Chess Game ‚ôüÔ∏è</h1>

        {/* üî• C·∫£nh b√°o chi·∫øu vua */}
        {isCheck && !isCheckMate && checkedKing && (
          <div className="fixed top-5 right-5 bg-red-600 text-white px-4 py-2 rounded-md shadow-lg font-bold animate-pulse">
            üî• Warning! {currentTurn ? "Black" : "White"} King is in Check! üî•
          </div>
        )}

        {/* üé≠ Th√¥ng b√°o tr·∫°ng th√°i game */}
        {gameMessage && (
          <div className="mb-2 bg-yellow-300 text-black px-4 py-2 rounded text-center font-bold shadow-md">
            {gameMessage}
          </div>
        )}

        <div className="flex gap-8">
          {/* üéØ B√†n c·ªù c√≥ th·ªÉ xoay */}
          <div className={`grid grid-cols-8 border-4 border-black shadow-lg ${isBlack ? "rotate-180" : ""}`}>
            {board.map((row, rowIndex) =>
              row.map((piece, colIndex) => (
                <Square
                  key={`${rowIndex}-${colIndex}`}
                  piece={piece}
                  row={rowIndex}
                  col={colIndex}
                  movePiece={movePiece}
                  isKingSquare={checkedKing?.row === rowIndex && checkedKing?.col === colIndex}
                  selectedPiece={selectedPiece}
                  setSelectedPiece={setSelectedPiece}
                  currentTurn={currentTurn}
                  board={board}
                  validMoves={selectedPiece ? getValidMoves(selectedPiece.piece, selectedPiece.row, selectedPiece.col, board) : []}
                />
              ))
            )}
          </div>

          {/* üìú L·ªãch s·ª≠ n∆∞·ªõc ƒëi */}
          <div className="w-64 bg-white p-4 rounded-lg shadow-md h-fit">
            <h2 className="text-lg font-semibold mb-2">üìú Move History</h2>
            <ul className="list-decimal pl-5">
              {moveHistory.map((move, index) => <li key={index}>{move}</li>)}
            </ul>
          </div>
        </div>

        {/* üîÑ N√∫t reset game */}
        <button onClick={resetGame} className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">
          Restart Game
        </button>
      </div>
    </DndProvider>
  );
};

export default ChessBoard;
