import React from "react";
import { useDrag, useDrop } from "react-dnd";

const ItemTypes = { PIECE: "piece" };

// Tạo quân cờ bằng SVG
const pieceSVG = {
  "♙": <circle cx="50%" cy="50%" r="40%" fill="white" />,
  "♖": <rect x="25%" y="25%" width="50%" height="50%" fill="white" />,
  "♘": <polygon points="50,10 90,90 10,90" fill="white" />,
  "♗": <ellipse cx="50%" cy="50%" rx="25%" ry="40%" fill="white" />,
  "♕": <polygon points="50,10 90,40 80,90 20,90 10,40" fill="white" />,
  "♔": <polygon points="50,10 80,40 70,90 30,90 20,40" fill="white" />,
  "♟": <circle cx="50%" cy="50%" r="40%" fill="black" />,
  "♜": <rect x="25%" y="25%" width="50%" height="50%" fill="black" />,
  "♞": <polygon points="50,10 90,90 10,90" fill="black" />,
  "♝": <ellipse cx="50%" cy="50%" rx="25%" ry="40%" fill="black" />,
  "♛": <polygon points="50,10 90,40 80,90 20,90 10,40" fill="black" />,
  "♚": <polygon points="50,10 80,40 70,90 30,90 20,40" fill="black" />,
};

const Square = ({ piece, row, col, movePiece, turn, validMoves }) => {
  const isDark = (row + col) % 2 === 1;
  const isHighlighted = validMoves.some((move) => move.row === row && move.col === col);

  const [{ isDragging }, dragRef] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    canDrag: () => (turn === "white" && "♙♖♘♗♕♔".includes(piece)) || (turn === "black" && "♟♜♞♝♛♚".includes(piece)),
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, dropRef] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  return (
    <div ref={dropRef} className={`w-16 h-16 flex justify-center items-center ${isDark ? "bg-green-600" : "bg-white"} ${isHighlighted ? "ring-4 ring-blue-400" : ""}`}>
      {piece && <svg ref={dragRef} width="80%" height="80%" viewBox="0 0 100 100">{pieceSVG[piece]}</svg>}
    </div>
  );
};

export default Square;
