import React, { useState, useEffect } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import Square from "./Square";
import { initialBoardSetup } from "../data/chessPieces";
import { isValidMove, getAllPossibleMoves, isKingInCheck } from "../utils/moveRules";

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [moveHistory, setMoveHistory] = useState([]);
  const [turn, setTurn] = useState("white");
  const [checkStatus, setCheckStatus] = useState(null);
  const [validMoves, setValidMoves] = useState([]);

  useEffect(() => {
    const kingInCheck = isKingInCheck(turn, board);
    setCheckStatus(kingInCheck ? `${turn.toUpperCase()} is in CHECK!` : null);
  }, [board, turn]);

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];

      if (!isValidMove(piece, fromRow, fromCol, toRow, toCol, newBoard, turn)) return prevBoard;

      newBoard[toRow][toCol] = piece;
      newBoard[fromRow][fromCol] = "";

      setMoveHistory((prevHistory) => [
        ...prevHistory,
        `${piece} ${convertToChessNotation(fromRow, fromCol)} → ${convertToChessNotation(toRow, toCol)}`,
      ]);

      setTurn(turn === "white" ? "black" : "white");
      setValidMoves([]);
      return newBoard;
    });
  };

  const handleSelectPiece = (row, col, piece) => {
    if ((turn === "white" && "♙♖♘♗♕♔".includes(piece)) || (turn === "black" && "♟♜♞♝♛♚".includes(piece))) {
      setValidMoves(getAllPossibleMoves(piece, row, col, board));
    }
  };

  const convertToChessNotation = (row, col) => {
    const files = ["a", "b", "c", "d", "e", "f", "g", "h"];
    return `${files[col]}${8 - row}`;
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="flex flex-col items-center">
        <h2 className="text-xl font-bold mb-2">{turn.toUpperCase()}’s Turn</h2>
        {checkStatus && <div className="text-red-500 font-bold">{checkStatus}</div>}

        <div className="flex">
          <div className="grid grid-cols-8 w-[512px] h-[512px] border-4 border-black relative">
            {board.map((row, rowIndex) =>
              row.map((piece, colIndex) => (
                <Square
                  key={`${rowIndex}-${colIndex}`}
                  piece={piece}
                  row={rowIndex}
                  col={colIndex}
                  movePiece={movePiece}
                  turn={turn}
                  validMoves={validMoves}
                />
              ))
            )}

            {/* Hàng ngang (a-h) */}
            <div className="absolute bottom-0 w-full flex justify-between px-2 text-gray-700 text-sm">
              {["a", "b", "c", "d", "e", "f", "g", "h"].map((letter, index) => (
                <span key={index} className="w-8 text-center">{letter}</span>
              ))}
            </div>

            {/* Hàng dọc (1-8) */}
            <div className="absolute left-0 h-full flex flex-col justify-between py-2 text-gray-700 text-sm">
              {["8", "7", "6", "5", "4", "3", "2", "1"].map((number, index) => (
                <span key={index} className="h-8 text-center">{number}</span>
              ))}
            </div>
          </div>
        </div>
      </div>
    </DndProvider>
  );
};

export default Chessboard;
