let currentTurn = true; // true lÃ  Tráº¯ng, false lÃ  Äen

export const isValidMove = (piece, fromRow, fromCol, toRow, toCol, board) => {
  if (fromRow === toRow && fromCol === toCol) return false;

  const isWhite = "â™™â™–â™˜â™—â™•â™”".includes(piece);
  if (isWhite !== currentTurn) return false; // ðŸ”¥ Fix lá»—i kiá»ƒm tra lÆ°á»£t

  const targetPiece = board[toRow][toCol];

  // KhÃ´ng thá»ƒ Ä‘i vÃ o Ã´ chá»©a quÃ¢n Ä‘á»“ng minh
  if (targetPiece && "â™™â™–â™˜â™—â™•â™”".includes(targetPiece) === isWhite) return false;

  let validMove = false;
  switch (piece) {
    case "â™™": validMove = isValidPawnMove(fromRow, fromCol, toRow, toCol, board, true); break;
    case "â™Ÿ": validMove = isValidPawnMove(fromRow, fromCol, toRow, toCol, board, false); break;
    case "â™–": case "â™œ": validMove = isValidRookMove(fromRow, fromCol, toRow, toCol, board); break;
    case "â™˜": case "â™ž": validMove = isValidKnightMove(toCol - fromCol, toRow - fromRow); break;
    case "â™—": case "â™": validMove = isValidBishopMove(fromRow, fromCol, toRow, toCol, board); break;
    case "â™•": case "â™›": validMove = isValidQueenMove(fromRow, fromCol, toRow, toCol, board); break;
    case "â™”": case "â™š": validMove = isValidKingMove(toCol - fromCol, toRow - fromRow); break;
    default: return false;
  }

  if (validMove) {
    // Giáº£ láº­p bÃ n cá» sau nÆ°á»›c Ä‘i
    const newBoard = board.map((row) => [...row]);
    newBoard[toRow][toCol] = piece;
    newBoard[fromRow][fromCol] = "";

    // Kiá»ƒm tra nÆ°á»›c Ä‘i cÃ³ lÃ m vua bá»‹ chiáº¿u khÃ´ng
    if (isKingInCheck(newBoard, isWhite)) return false;
    return true;
  }

  return false;
};

// âœ… TÃ¡ch logic Ä‘á»•i lÆ°á»£t vÃ o `movePiece()`
export const movePiece = (fromRow, fromCol, toRow, toCol, board) => {
  if (isValidMove(board[fromRow][fromCol], fromRow, fromCol, toRow, toCol, board)) {
    board[toRow][toCol] = board[fromRow][fromCol];
    board[fromRow][fromCol] = "";

    currentTurn = !currentTurn; // ðŸ”¥ Chá»‰ Ä‘á»•i lÆ°á»£t sau khi di chuyá»ƒn thÃ nh cÃ´ng

    renderBoard();
  }
};
