import React from "react";
import { useDrag, useDrop } from "react-dnd";

const ItemTypes = { PIECE: "piece" };

const Square = ({ piece, row, col, movePiece, turn, validMoves }) => {
  const isDark = (row + col) % 2 === 1;
  const isHighlighted = validMoves.some((move) => move.row === row && move.col === col);

  const [{ isDragging }, dragRef] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    canDrag: () => (turn === "white" && "♙♖♘♗♕♔".includes(piece)) || (turn === "black" && "♟♜♞♝♛♚".includes(piece)),
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, dropRef] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  return (
    <div
      ref={dropRef}
      className={`w-16 h-16 flex justify-center items-center text-2xl font-bold relative 
        ${isDark ? "bg-[#769656]" : "bg-[#eeeed2]"} 
        ${isHighlighted ? "ring-4 ring-blue-400" : ""}`}
    >
      {piece && (
        <img
          ref={dragRef}
          src={`/assets/${piece}.png`}
          alt={piece}
          className={`w-12 h-12 cursor-grab transition-transform transform ${
            isDragging ? "scale-90 opacity-50" : "scale-100"
          }`}
        />
      )}
    </div>
  );
};

export default Square;
