import React, { useState } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { isValidMove } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate, findKingPosition } from "../utils/checkmate"; 
import pieceImages from "../assets/pieceImages";

const ItemTypes = { PIECE: "piece" };
const [validMoves, setValidMoves] = useState([]);

const Square = ({ piece, row, col, movePiece, isKingInCheck, selectedPiece, setSelectedPiece, currentTurn }) => {

  const isDark = (row + col) % 2 === 1;
  const isSelected = selectedPiece && selectedPiece.row === row && selectedPiece.col === col;

  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PIECE,
    item: { row, col, piece },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop(() => ({
    accept: ItemTypes.PIECE,
    drop: (item) => movePiece(item.row, item.col, row, col),
  }));

  const handleClick = () => {
    if (piece) {
      // Ki·ªÉm tra xem qu√¢n c·ªù c√≥ thu·ªôc v·ªÅ ng∆∞·ªùi ch∆°i hi·ªán t·∫°i kh√¥ng
      const isWhite = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);
      const isBlack = "‚ôü‚ôú‚ôû‚ôù‚ôõ‚ôö".includes(piece);

      if ((isWhite && currentTurn) || (isBlack && !currentTurn)) {
        setSelectedPiece({ row, col, piece }); // Ch·ªçn qu√¢n c·ªù
        const moves = getValidMoves(piece, row, col, board); // L·∫•y n∆∞·ªõc ƒëi h·ª£p l·ªá
        setValidMoves(moves); // L∆∞u danh s√°ch n∆∞·ªõc ƒëi
      }
    } else if (selectedPiece) {
      // N·∫øu ƒë√£ ch·ªçn qu√¢n c·ªù tr∆∞·ªõc ƒë√≥, th·ª±c hi·ªán di chuy·ªÉn
      movePiece(selectedPiece.row, selectedPiece.col, row, col);
      setValidMoves([]); // Reset n∆∞·ªõc ƒëi sau khi di chuy·ªÉn
    }
  };




  return (
    <div
      ref={drop}
      className={`w-16 h-16 flex justify-center items-center 
        ${isDark ? "bg-[#9A6553]" : "bg-[#D5B793]"}
        ${isKingInCheck ? "border-4 border-red-500" : ""}
        ${isSelected ? "border-4 border-blue-500" : ""}
      `}
      onClick={handleClick}
    >
      {piece && pieceImages[piece] && (
        <img
          ref={drag}
          src={pieceImages[piece]}
          alt={piece}
          className={`w-12 h-12 object-contain ${isDragging ? "opacity-50" : "cursor-pointer"}`}
        />
      )}
    </div>
  );
};


const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [moveHistory, setMoveHistory] = useState([]);
  const [currentTurn, setCurrentTurn] = useState(true); // true: Tr·∫Øng, false: ƒêen
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);
  const [checkedKing, setCheckedKing] = useState(null);
  const [selectedPiece, setSelectedPiece] = useState(null); // ‚úÖ Ch·ªâ ƒë·ªÉ trong Chessboard

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map(row => [...row]);
      const piece = newBoard[fromRow][fromCol];

      if (isValidMove(piece, fromRow, fromCol, toRow, toCol, newBoard)) {
        newBoard[toRow][toCol] = piece;
        newBoard[fromRow][fromCol] = "";

        const isWhiteTurn = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);
        setCurrentTurn(!currentTurn);

        const isNowCheck = isKingInCheck(newBoard, !isWhiteTurn);
        setIsCheck(isNowCheck);
        setCheckedKing(isNowCheck ? findKingPosition(newBoard, !isWhiteTurn) : null);
        setIsCheckMate(isCheckmate(newBoard, !isWhiteTurn));

        setMoveHistory([...moveHistory, `${piece} ${convertToChessNotation(fromRow, fromCol)} ‚ûù ${convertToChessNotation(toRow, toCol)}`]);

        setSelectedPiece(null); // Ch·ªâ reset khi di chuy·ªÉn th√†nh c√¥ng
        return newBoard;
      }

      return prevBoard;
    });
  };


  const convertToChessNotation = (row, col) => {
    const files = ["a", "b", "c", "d", "e", "f", "g", "h"];
    return `${files[col]}${8 - row}`;
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen flex flex-col justify-center items-center bg-gray-200">
        <h1 className="text-2xl font-bold mb-4">‚ôüÔ∏è Chess Game ‚ôüÔ∏è</h1>

        <div className="mb-2">
          {isCheckMate ? (
            <div className="text-red-800 text-lg font-bold">üèÜ Chi·∫øu h·∫øt! Tr√≤ ch∆°i k·∫øt th√∫c üèÜ</div>
          ) : isCheck ? (
            <div className="text-red-600 text-lg font-bold">üî• Vua ƒëang b·ªã chi·∫øu! üî•</div>
          ) : (
            <div className="text-lg font-bold">üé≠ First move: {currentTurn ? "White" : "Black"}</div>
          )}
        </div>

        <div className="grid grid-cols-8 border-4 border-black shadow-lg">
          {board.map((row, rowIndex) =>
            row.map((piece, colIndex) => (
              <Square
                key={`${rowIndex}-${colIndex}`}
                piece={piece}
                row={rowIndex}
                col={colIndex}
                movePiece={movePiece}
                isKingInCheck={checkedKing?.row === rowIndex && checkedKing?.col === colIndex}
                selectedPiece={selectedPiece}
                setSelectedPiece={setSelectedPiece}
              />
            ))
          )}
        </div>

        <div className="mt-4 bg-white p-4 rounded shadow-md">
          <h2 className="text-lg font-bold mb-2">üìú Move History</h2>
          <ul className="text-sm text-gray-700 h-32 overflow-auto">
            {moveHistory.map((move, index) => (
              <li key={index}>{index + 1}. {move}</li>
            ))}
          </ul>
        </div>
      </div>
    </DndProvider>
  );
};

export default Chessboard;
