import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import Square from "./Square";
import { isValidMove } from "../utils/moveRules"; // Đảm bảo đường dẫn đúng

const initialBoardSetup = [
  ["♜", "♞", "♝", "♛", "♚", "♝", "♞", "♜"],
  ["♟", "♟", "♟", "♟", "♟", "♟", "♟", "♟"],
  ["", "", "", "", "", "", "", ""],
  ["", "", "", "", "", "", "", ""],
  ["", "", "", "", "", "", "", ""],
  ["", "", "", "", "", "", "", ""],
  ["♙", "♙", "♙", "♙", "♙", "♙", "♙", "♙"],
  ["♖", "♘", "♗", "♕", "♔", "♗", "♘", "♖"],
];

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [isWhiteTurn, setIsWhiteTurn] = useState(true); // Trắng đi trước

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];

      // Kiểm tra nếu di chuyển hợp lệ
      if (!isValidMove(piece, fromRow, fromCol, toRow, toCol, board, isWhiteTurn)) {
        console.log("Invalid move!");
        return prevBoard;
      }

      // Thực hiện di chuyển quân cờ
      newBoard[toRow][toCol] = piece;
      newBoard[fromRow][fromCol] = "";

      // Đổi lượt đi
      setIsWhiteTurn(!isWhiteTurn);

      return newBoard;
    });
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div
        className="h-screen w-screen flex flex-col items-center justify-center bg-cover bg-center"
        style={{ backgroundImage: "url('https://source.unsplash.com/1920x1080/?chess,board')" }}
      >
        <h1 className="text-4xl font-bold mb-6 text-white bg-black bg-opacity-50 p-3 rounded-lg shadow-lg">
          Chess Game
        </h1>
        <p className="text-lg font-semibold text-white bg-black bg-opacity-50 px-4 py-2 rounded-lg shadow-lg mb-4">
          {isWhiteTurn ? "White's Turn (♙♖♘♗♕♔)" : "Black's Turn (♟♜♞♝♛♚)"}
        </p>
        <div className="grid grid-cols-8 border-8 border-gray-800 shadow-2xl rounded-xl bg-gray-900 bg-opacity-70 p-1">
          {board.map((row, rowIndex) =>
            row.map((piece, colIndex) => (
              <Square
                key={`${rowIndex}-${colIndex}`}
                piece={piece}
                row={rowIndex}
                col={colIndex}
                movePiece={movePiece}
              />
            ))
          )}
        </div>
      </div>
    </DndProvider>
  );
};

export default Chessboard;
