import React, { useState } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { isValidMove } from "../utils/moveRules";
import { initialBoardSetup } from "../config/boardSetup";
import { isKingInCheck, isCheckmate, findKingPosition } from "../utils/checkmate"; 
import pieceImages from "../assets/pieceImages";

const ItemTypes = { PIECE: "piece" };

const Square = ({ piece, row, col, onClick, isHighlighted, isKingInCheck }) => {
  const isDark = (row + col) % 2 === 1;

  return (
    <div
      onClick={() => onClick(row, col)}
      className={`w-16 h-16 flex justify-center items-center cursor-pointer 
        ${isDark ? "bg-[#9A6553]" : "bg-[#D5B793]"} 
        ${isKingInCheck ? "border-4 border-red-500" : ""} 
        ${isHighlighted ? "bg-green-400" : ""} 
      `}
    >
      {piece && pieceImages[piece] && (
        <img
          src={pieceImages[piece]}
          alt={piece}
          className="w-12 h-12 object-contain"
        />
      )}
    </div>
  );
};

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [validMoves, setValidMoves] = useState([]);
  const [isCheck, setIsCheck] = useState(false);
  const [isCheckMate, setIsCheckMate] = useState(false);
  const [checkedKing, setCheckedKing] = useState(null);

  const handleSquareClick = (row, col) => {
    const piece = board[row][col];

    if (selectedPiece) {
      // N·∫øu ƒë√£ ch·ªçn qu√¢n c·ªù v√† b·∫•m v√†o √¥ h·ª£p l·ªá -> di chuy·ªÉn qu√¢n c·ªù
      if (validMoves.some(move => move.row === row && move.col === col)) {
        movePiece(selectedPiece.row, selectedPiece.col, row, col);
        setSelectedPiece(null);
        setValidMoves([]);
        return;
      } else {
        // N·∫øu b·∫•m v√†o qu√¢n kh√°c -> ch·ªçn l·∫°i
        setSelectedPiece(null);
        setValidMoves([]);
      }
    }

    // N·∫øu b·∫•m v√†o qu√¢n c·ªù m·ªõi, t√¨m √¥ h·ª£p l·ªá
    if (piece) {
      const possibleMoves = [];
      for (let r = 0; r < 8; r++) {
        for (let c = 0; c < 8; c++) {
          if (isValidMove(piece, row, col, r, c, board)) {
            possibleMoves.push({ row: r, col: c });
          }
        }
      }

      if (possibleMoves.length > 0) {
        setSelectedPiece({ row, col });
        setValidMoves(possibleMoves);
      }
    }
  };

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    setBoard((prevBoard) => {
      const newBoard = prevBoard.map(row => [...row]);
      const piece = newBoard[fromRow][fromCol];

      if (isValidMove(piece, fromRow, fromCol, toRow, toCol, newBoard)) {
        newBoard[toRow][toCol] = piece;
        newBoard[fromRow][fromCol] = "";

        const isWhiteTurn = "‚ôô‚ôñ‚ôò‚ôó‚ôï‚ôî".includes(piece);
        const isNowCheck = isKingInCheck(newBoard, !isWhiteTurn);
        setIsCheck(isNowCheck);

        if (isNowCheck) {
          setCheckedKing(findKingPosition(newBoard, !isWhiteTurn));
        } else {
          setCheckedKing(null);
        }

        setIsCheckMate(isCheckmate(newBoard, !isWhiteTurn));

        return newBoard;
      }

      return prevBoard;
    });
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gray-200">
      <h1 className="text-2xl font-bold mb-4">‚ôüÔ∏è Chess Game ‚ôüÔ∏è</h1>

      {isCheck && <div className="text-red-600 text-lg font-bold">üî• Vua ƒëang b·ªã chi·∫øu! üî•</div>}
      {isCheckMate && <div className="text-red-800 text-lg font-bold">üèÜ Chi·∫øu h·∫øt! Tr√≤ ch∆°i k·∫øt th√∫c üèÜ</div>}

      <div className="grid grid-cols-8 border-4 border-black shadow-lg">
        {board.map((row, rowIndex) =>
          row.map((piece, colIndex) => (
            <Square
              key={`${rowIndex}-${colIndex}`}
              piece={piece}
              row={rowIndex}
              col={colIndex}
              onClick={handleSquareClick}
              isHighlighted={validMoves.some(m => m.row === rowIndex && m.col === colIndex)}
              isKingInCheck={checkedKing?.row === rowIndex && checkedKing?.col === colIndex}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default Chessboard;
