import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import Square from "./Square";
import { isValidMove, checkGameOver } from "../utils/moveRules"; // Import thêm checkGameOver

const initialBoardSetup = [
  ["♜", "♞", "♝", "♛", "♚", "♝", "♞", "♜"],
  ["♟", "♟", "♟", "♟", "♟", "♟", "♟", "♟"],
  ["", "", "", "", "", "", "", ""],
  ["", "", "", "", "", "", "", ""],
  ["", "", "", "", "", "", "", ""],
  ["", "", "", "", "", "", "", ""],
  ["♙", "♙", "♙", "♙", "♙", "♙", "♙", "♙"],
  ["♖", "♘", "♗", "♕", "♔", "♗", "♘", "♖"],
];

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoardSetup);
  const [isWhiteTurn, setIsWhiteTurn] = useState(true);
  const [gameOver, setGameOver] = useState(null);

  const movePiece = (fromRow, fromCol, toRow, toCol) => {
    if (gameOver) return;

    setBoard((prevBoard) => {
      const newBoard = prevBoard.map((row) => [...row]);
      const piece = newBoard[fromRow][fromCol];

      if (!isValidMove(piece, fromRow, fromCol, toRow, toCol, board, isWhiteTurn)) return prevBoard;

      newBoard[toRow][toCol] = piece;
      newBoard[fromRow][fromCol] = "";

      setIsWhiteTurn(!isWhiteTurn);

      // Kiểm tra thắng/thua
      const result = checkGameOver(newBoard);
      if (result) setGameOver(result);

      return newBoard;
    });
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="h-screen flex flex-col items-center justify-center">
        <h1 className="text-3xl font-bold">{gameOver || (isWhiteTurn ? "White's Turn" : "Black's Turn")}</h1>
        <div className="grid grid-cols-8 border-4 border-black">
          {board.map((row, rowIndex) =>
            row.map((piece, colIndex) => (
              <Square key={`${rowIndex}-${colIndex}`} piece={piece} row={rowIndex} col={colIndex} movePiece={movePiece} />
            ))
          )}
        </div>
      </div>
    </DndProvider>
  );
};

export default Chessboard;
